import React, { useState } from 'react';
import './LoginPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-regular-svg-icons';

const LoginPage = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [password, setPassword] = useState('');

    const handleShowPassword = (e) => {
        setPassword(e.target.value);
    }
    return (
        <div className="login-page">
        <div className="login-form login-page-container">
            <h2>Welcome to <span className='logo-name'>Workflo</span>!</h2>
            <form>
                <input
                type="email"
                placeholder="Email" 
                />
                <input
                type="text"
                placeholder="Password"
                value={showPassword ? password : '*'.repeat(password.length)}
                onChange={handleShowPassword}
                />
            <button type="submit" className='isFormComplete ? btn-enabled : btn-disabled'>Login</button>
            <FontAwesomeIcon icon={showPassword ? faEye : faEyeSlash} className='faEyeSlash' onClick={() => setShowPassword(prev => !prev)}/>
            </form>
            <p>Don't have an account? <a href="#">Create a new account.</a></p>
        </div>
        </div>
    )
}

export default LoginPage;
