import React from 'react';
import './LoginPage.css';

const LoginPage = () => {
  return (
    <div className="login-page">
      <div className="login-form login-page-container">
        <h2>Welcome to <span className='logo-name'>Workflo</span>!</h2>
        <form>
            <input
              type="email"
              placeholder="Email" 
            />
            <input
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              ref={passwordInputRef}
            />
          <button type="submit">Login</button>
          <FontAwesomeIcon icon={faEyeSlash} className='faEyeSlash' onClick={handleShowPassword}/>
        </form>
        <p>Don't have an account? <a href="#">Create a new account.</a></p>
      </div>
    </div>
  )
}

export default LoginPage;
